a) {\n                    data=(new Function(\'return \' + data))();\n                    if(data.status==0){\n                        data=data.data;\n                        var plusNode = node.children[0];\n                        plusNode.style[\"display\"] = \"inline\";\n                        var clickNode = node.parentNode.parentNode.getElementsByTagName(\"a\");\n                        for (var i = 0; i < clickNode.length; i++) {\n                            var element = clickNode[i].children[0];\n                            element.removeAttribute(\"onclick\");\n                            element.style[\"backgroundPosition\"] = \"right center\";\n                        }\n                        transition(plusNode, {\n                            field: \'top\',\n                            begin: -40,\n                            change: -27,\n                            ease: tween.easeOutQuart,\n                            duration: 1000\n                        });\n                        opacityTransition(plusNode, {\n                            duration: 1000\n                        });\n\n                        var content=node.parentNode.parentNode;\n                        var competeVote=content.parentNode;\n                        var leftTitle=content.children[1].innerText;\n                        var leftBar=competeVote.children[1].children[2];\n                        var leftPercentWhite=competeVote.children[1].children[2].children[0];\n                        var leftPercentBlack=competeVote.children[1].children[4];\n                        var leftNum=competeVote.children[1].children[0].children[0];\n                        var rightBar=competeVote.children[1].children[3];\n                        var rightPercentWhite=competeVote.children[1].children[3].children[0];\n                        var rightPercentBlack=competeVote.children[1].children[5];\n                        var rightNum=competeVote.children[1].children[0].children[2];\n                        var barWidth=(competeVote.children[1].offsetWidth-214)/2;\n                        var leftData;\n                        var rightData;\n                        if(data[0].data[0].name==leftTitle){\n                            leftData=data[0].data[0];\n                            rightData=data[0].data[1];\n                        }else{\n                            leftData=data[0].data[1];\n                            rightData=data[0].data[0];\n                        }\n                        leftPercentBlack.innerText=leftData.percent;\n                        leftPercentWhite.innerText=leftData.percent;\n                        leftNum.innerText=leftData.num;\n                        rightPercentBlack.innerText=rightData.percent;\n                        rightPercentWhite.innerText=rightData.percent;\n                        rightNum.innerText=rightData.num;\n                        var leftBarOriginWid=parseInt(leftBar.style.width,10);\n                        var leftBarWidthAdd=(parseInt(leftData.percent,10)*barWidth)/100-leftBarOriginWid;\n                        var rightBarOriginWid=parseInt(rightBar.style.width,10);\n                        var rightBarWidthAdd=(parseInt(rightData.percent,10)*barWidth)/100-rightBarOriginWid;\n                        transition(leftBar, {\n                            field: \'width\',\n                            begin: leftBarOriginWid,\n                            change: leftBarWidthAdd,\n                            ease: tween.easeOutQuart,\n                            duration: 1000\n                        });\n                        transition(rightBar, {\n                            field: \'width\',\n                            begin: rightBarOriginWid,\n                            change: rightBarWidthAdd,\n                            ease: tween.easeOutQuart,\n                            duration: 1000\n                        });\n\n                    }else{\n                        dialog(data);\n                        setTimeout(function(){\n                            window.location.reload();\n                        },3100);\n                    }\n                });\n            } else if (style === \"select\") {\n                var selectPanel = node.parentNode.parentNode;\n\n                var topicSet = selectPanel.children[0];\n                var oid = [];\n                var flag = true; // 用户选择标识\n                for (var i = 0; i < topicSet.children.length; i++) {\n                    var content = topicSet.children[i].children[1];\n                    var dataOptionBottom = parseInt(content.attributes[\"data-option-bottom\"].value, 10);\n                    var selected = parseInt(content.attributes[\"data-selected\"].value, 10);\n                    var dataOptionId = content.attributes[\"data-option-id\"].value;\n\n                    if (selected < dataOptionBottom) {\n                        content.setAttribute(\"class\",\"content error\");\n                        flag = false;  // 用户没有选择正确数量的选项\n                    } else if (!!dataOptionId) {\n                        oid = oid.concat(dataOptionId.split(\",\"));\n                    }\n                }\n                //console.log(oid);\n                if (flag) {\n                    node.setAttribute(\"onclick\",\"\");\n                    node.children[0].style.display=\"none\";\n                    node.children[2].style.display=\"inline\";\n                    postAjax(\'/app.php?m=vote&a=addVote\', {\n                        \"id\": oid,\n                        \"vid\": vid\n                    }, function(data) {\n                        data=(new Function(\'return \' + data))();\n                        if(data.status==0){\n                            selectPanel.style[\"display\"] = \"none\";\n                            var resultPanel = selectPanel.parentNode.children[1];\n                            resultPanel.style[\"display\"] = \"block\";\n