ttributes[\"data-select\"].value = \"checked\";\n                    el.style[\"backgroundPosition\"] = \"left center\";\n\n                    content.setAttribute(\"data-selected\", \"1\");\n                    content.setAttribute(\"data-option-id\", id);\n                }\n            } else if (row.attributes[\"data-select\"].value == \"checked\") {\n                content.setAttribute(\"data-selected\", --selected);\n                el.style[\"backgroundPosition\"] = \"right center\";\n                row.setAttribute(\"data-select\", \"\");\n\n                dataOptionId.splice(dataOptionId.indexOf(id), 1);\n                content.setAttribute(\"data-option-id\", dataOptionId.join(\",\"));\n            } else if (selected < dataOptionTop) {\n                content.setAttribute(\"data-selected\", ++selected);\n                el.style[\"backgroundPosition\"] = \"left center\";\n                row.setAttribute(\"data-select\", \"checked\");\n\n                dataOptionId.push(id);\n                content.setAttribute(\"data-option-id\", dataOptionId.join(\",\"));\n            }\n            // 去除未正确选择时的错误提示\n            if(content.getAttribute(\"class\")==\"content error\"){\n                content.setAttribute(\"class\",\"content\");\n                content.parentNode.parentNode.parentNode.children[1].children[0].children[1].innerText=\"\";\n            }\n        }\n\n        function postAjax(url, data, success) {\n            var params;\n            if (typeof data == \"string\") {\n                params = data;\n            } else {\n                var list = [];\n                for (var i in data) {\n                    list.push(encodeURIComponent(i) + \'=\' + encodeURIComponent(data[i]));\n                }\n                params = list.join(\'&\');\n            }\n\n            var xhr = window.XMLHttpRequest ? new XMLHttpRequest() : new ActiveXObject(\"Microsoft.XMLHTTP\");\n            xhr.open(\'POST\', url);\n            xhr.onreadys